function bindStatistics(){$(document).bind("pageshow",function(d,f){var a=$.parseJSON(localStorage.stats_data);for(var c=0;c<a.length;++c){a[c][0]*=1000}function e(l){var j=[];var m=new Date(l.xaxis.min);m.setUTCDate(m.getUTCDate()-((m.getUTCDay()+1)%7));m.setUTCSeconds(0);m.setUTCMinutes(0);m.setUTCHours(0);var k=m.getTime();do{j.push({xaxis:{from:k,to:k+2*24*60*60*1000}});k+=7*24*60*60*1000}while(k<l.xaxis.max);return j}var b={xaxis:{mode:"time"},selection:{mode:"x"},grid:{markings:e}};var g=$.plot($("#stats-placeholder"),[a],b);var h=$.plot($("#stats-overview"),[a],{series:{lines:{show:true,lineWidth:1},shadowSize:0},xaxis:{ticks:[],mode:"time"},yaxis:{ticks:[],min:0,autoscaleMargin:0.1},selection:{mode:"x"}});$("#stats-placeholder").bind("plotselected",function(j,i){g=$.plot($("#stats-placeholder"),[a],$.extend(true,{},b,{xaxis:{min:i.xaxis.from,max:i.xaxis.to}}));h.setSelection(i,true)});$("#stats-overview").bind("plotselected",function(j,i){g.setSelection(i)})})};
